INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(145, 22, 'arya', 'Raih Empat Gelar Kontes Gitar Internasional Hebat, Gitar Produk Sidoarjo Diakui Dunia', 'raih-empat-gelar-kontes-gitar-internasional-hebat-gitar-produk-sidoarjo-diakui-dunia', 'Y', '<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\n<strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent">LENSAINDONESI.COM:&nbsp;</strong>Bagi penggemar Gitar, apalagi pemula, Gitar merk Rick Hanes mungkin belum banyak dikenal. Namun bagi mereka yang sering mengikuti perkembangan Gitar, merk baru ini begitu fenomenal.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nBagaimana tidak, Gitar produk asli Sidoarjo, Jawa Timur itu mampu berbicara di tingkat internasional, bahkan mampu merebut empat gelar sekaligus dalam kontes Guitar Of The Years 2012 di Inggris beberapa waktu lalu.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nGitar merk Rick Hanes tersebut dikerjakan di pabriknya, di kawasan industri Tambak Sawah nomor 5 Kecamatan Waru, Sidoarjo. Disini pula 60 karyawan mengerjakan tiap bagian-bagian Gitar dengan sangat teliti. Mulai dari pembuatan body, pembuatan leher Gitar, pemasangan Fred, hingga pengecatan.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nTak cukup hanya dengan pengejaan manual, pembuatan gitar tersebut juga menggunakan dua buah mesin dari Jerman. Di kawasan Asia, mesin ini hanya terdapat di Jepang dan di Indonesia (Tambak Sawah, Sidoarjo).\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nYang membuat Gitar ini menjadi produk berkualitas adalah ditanamkannya Carbon Graphite pada Neck Gitar. Jenis Carbon ini merupakan bahan pembuat body pesawat ulang alik (Pesawat Nasa) yang terkenal kuat namun lentur dan ringan. Bahkan kayu Mample yang digunakan pada Neck dan body juga bahan import.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nBagi Endorse atau salah satu pemilik pabrik Gitar Rick Hanes, Doddy Hernanto yang akrab dipanggila Mr. D, juga memproduksi Gitar merk Rock Stone yang dipasarkan khusus di Indonesia.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nAda 4 indikasi hingga gitar rock Hanes menjadi juara dunia, antara lain, play-abality atau kemudahan dimainkan, Soud Aesthetic atau kemerduan suara, estetika serta kekeuatannya. Bahkan gitar produk dalam negeri ini tak mengalami kerusakan saat dibanting maupun diinjak.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nSalah satu produk Gitar Rick Hanes juga mampu diintregasikan secara langsung dengan produk Gadget semacam I-POD maupun I-Phone.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nMr. D mengusung produk gitarnya berbeda dengan pakem bermain Gitar pada umumnya, yakni menggunakan satu Jari.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nSejak kemenangan Rick Hanes di luar negeri, penggemar Gitar di tanah air pun mulai melirik Gitar tersebut. Meski harganya masih di kisaran Rp 8 juta hingga 15 juta, mereka tetap membelinya.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\n&ldquo;Kami ini penggemar Gitar, meskipun harganya agak mahal ya kami tetap suka dan membelinya.&rdquo;ujar Saldi, pemain dan penggemar Gitar.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nSelain menjuarai kontes gitar, produk Rick Hanes ini sudah dipakai beberapa gitaris terkenal, antara lain, I Wayan Balawan, Taras Bistara, Aji Broken Bone, Donny Suhendera, Irul Five Minute dan lain-lain.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nBahkan Buddy Blaze yang juga perancang gitar milik Jimmy Page, pentolan Led Zeppelin saat berkunjung ke pabrik Rick Hanes serta memuji Gitar produk anak negeri ini.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nKedepan, Mr. D berupaya memadukan musik dari produk Gitar yang telah diakui dunia ini dengan berbagai kesenian dan budaya lokal Indonesia.\r\n</p>\r\n<p style="margin: 15px 0px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; color: #444444; font-family: CustomDINProRegular; line-height: 25px; text-align: justify">\r\nBapak Empat putra ini juga berharap kesenian lokal yang selama ini terpuruk, bisa bangkit dan lebih dikenal, terutama di kalangan pemuda.<strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent">@jani @sukombes</strong>\r\n</p>\r\n', 'Senin', '2013-01-21', '08:29:47', '88uploads--1--2013--01--12548-GITAR-rich-hONES-Chris-Bickley-DR-Pro-600B.jpg', 3979785, 'kerajinan', 0),
(130, 22, 'admin', 'Jalin Kesra RTSM Bidang Perdagangan Tahun 2013', 'jalin-kesra-rtsm-bidang-perdagangan-tahun-2013', 'Y', '<p>\r\n<span class="align-justify"><font face="courier new, courier" size="3">Surabaya - Pemerintah Provinsi Jawa\r\nTimur semakin dekat di hati rakyat Jawa Timur melalui Program Jalin Kesra (Jalan\r\nLain Menuju Kesejahteraan ) RTSM (Rumah Tangga Sangat Miskin). Hal ini sebagai\r\nwujud nyata dari slogan APBD untuk rakyat yang diimplementasikan oleh Gubernur\r\nSoekarwo melalui kebijakan Pro Poor dan Pro Job.</font></span>\r\n</p>\r\n<p>\r\n<font face="courier new, courier" size="3">Setelah 3 tahun berjalan sejak 2010 lalu, program ini sangat dirasakan manfaatnya oleh RTSM dimana tingkat ekonomi mereka secara keseluruhan dapat meningkat dilihat dari evaluasi yang dilakukan setiap semester oleh pihak JPC maupun SKPD.</font>\r\n</p>\r\n<p>\r\n<font face="courier new, courier" size="3">Ditahun 2013, program ini telah merambah ke wilayah kota yang mana ditahun-tahun sebelumnya RTSM yang berada di wilayah kota belum tersentuh oleh program Pakde Karwo dengan slogannya APBD untuk Wong Cilik ini. Program ini direncanakan akan menyasar kepada 7.068 RTSM yang tersebar di 319 desa/ kelurahan, 42 kecamatan di 9 kota di Jawa Timur.</font>\r\n</p>\r\n<span class="align-justify"></span>\r\n', 'Minggu', '2012-05-13', '14:13:36', '91DSC02509.jpg', 12262, '', 0),
(131, 22, 'admin', 'PASAR LELANG KOMODITI KE-70 DI PUSPA AGRO JEMUNDO', 'pasar-lelang-komoditi-ke70-di-puspa-agro-jemundo', 'Y', '<span class="align-justify">Puspa Agro Jemundo Sidoarjo menjadi tempat \r\ndiselenggarakannya pasar lelang komoditi lokal ke-70 oleh Dinas \r\nPerindustrian dan Perdagangan (Disperindag) Jatim, Selasa (29/6). Lelang\r\ntersebut merupakan kelanjutan dari&nbsp; lelang sebelumnya dan merupakan \r\nagenda rutin yang diselenggarakan setiap akhir bulan.\r\n</span>\r\n', 'Minggu', '2012-05-13', '14:15:04', '88pll-70.jpg', 17778, '', 0),
