INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(273, 22, 'Pipit', 'Sosialisasi Bahaya Penggunaan Bahan Berbahaya Pada Makanan', 'sosialisasi-bahaya-penggunaan-bahan-berbahaya-pada-makanan', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 0.25in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Semakin\r\nterbukanya pasar nasional sebagai akibat dari proses globalisasi ekonomi menuntut\r\nkonsumen untuk lebih teliti terhadap setiap produk yang beredar di pasaran mengingat banyaknya produsen yang bersaing secara tidak sehat. Oleh\r\nkarena itu diperlukan kesadaran, pengetahuan, kepedulian kemampuan dan\r\nkemandirian konsumen untuk melindungi dirinya serta menumbuh kembangkan sikap\r\nperilaku usaha yang jujur dan bertanggung jawab.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Berdasarkan\r\nfakta di lapangan, peredaran dan penggunaan <strong>B</strong>ahan\r\n<strong>B</strong>erbahaya (<strong>B2</strong>) terus mengalami peningkatan baik jenis maupun jumlahnya. Kondisi\r\nini tentu sangat riskan dan dapat memicu adanya penyalahgunaan peruntukan bahan\r\nberbahaya, utamanya untuk produk makanan dengan target pasar yang luas.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Dampak\r\nnegatif B2 sendiri tidak hanya dialami oleh konsumen namun juga bisa berakibat\r\npada produsen karena dapat mematikan potensi produsen lain yang jujur dan\r\nbertanggung jawab serta tidak mendidik produsen pangan dalam menghasilkan\r\nproduk berdaya saing berbasis mutu serta memenuhi aspek keamanan, kesehatan,\r\ndan keselamatan konsumen.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Berdasarkan\r\npertimbangan tersebut diperlukan perangkat peraturan perundang-undangan yaitu\r\nUndang-Undang Nomor 8 Tahun 1999 tentang Perlindungan Konsumen dan Peraturan\r\nMenteri Perdagangan Republik Indonesia Nomor: 44/M-DAG/PER/9/2009 tentang\r\nPengadaan, Distribusi dan Pengawasan Bahan Berbahaya.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;"><span>&nbsp;</span>Sebagai catatan, pelaku usaha atau produsen\r\napabila terbukti melanggar ketentuan bahan berbahaya B2 maka akan dikenakan\r\nsaksi berjenjang mulai pencabutan pengakuan IP-B2 / IT-B2, pencabutan izin\r\nusaha (SIUP-B2) serta penarikan barang dari peredaran. Salah satu tindakan\r\npreventif pemerintah ialah melakukan pengawasan intensif terhadap produsen dan toko\r\nyang menjual B2 serta penyuluhan baik kepada produsen maupun konsumen mengenai\r\nbahaya B2.</span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Penulis<span>&nbsp;&nbsp;&nbsp;&