INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(274, 22, 'Ditya', 'Bir Hilang dari Minimarket', 'bir-hilang-dari-minimarket', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 36pt" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Kekhawatiran\r\nterhadap dampak negatif dengan kemudahan akses membeli produk beralkohol\r\nterutama bagi generasi muda, menjadi salah satu alas an pemerintah melalui\r\nKementrian Perdagangan melarang penjualan minuman beralkohol golongan A (kadar alkohol\r\nkurang dari 5%) di minimarket. Aturan larangan penjualan minuman beralkohol di\r\nminimarket berdasarkan pada peraturan Menteri Perdagangan (Permendag) No.\r\n6/2015 tentang Pengendalian dan Pengawasan Terhadap Pengadaan, Peredaran, dan\r\nPenjualan Minuman Beralkohol.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Pemerintah\r\nProvinsi Jawa Timur melalui PPNS Perlindungan Konsumen dan Petugas Pengawas\r\nPeredaran Barang bersama instansi terkait di Kabupaten/Kota di Jawa Timur akan\r\nmengambil langkah tegas terhadap peredaran dan penjualan minuman beralkohol di\r\nminimarket di wilayah Jawa Timur mulai 16 April 2015. Di Surabaya langkah\r\npenertiban telah dilakukan oleh petugas Disperindag Prov. Jatim bersama\r\nDisperindag Kota Surabaya, Satpol PP kecamatan dan pusat. </span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Prosedur razia yang\r\ndilakukan nantinya bisa dilakukan dua macam: menyita barang dagangan lalu\r\ndengan beberapa syarat pemenuhan administrative, lalu produk bisa dikembalikan.\r\nNamun, bisa juga dibuatkan laporan berita acara peringatan di tempat tanpa\r\nmelakukan perampasan produk. Dalam Pemendag, yang nantinya boleh menjual\r\nminuman alkohol hanya supermarket, hypermarket, restoran, hotel dan juga bar\r\nyang memiliki ijin khusus untuk menjual produk-produk minuman keras tersebut.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Penulis :-</span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Editor : Ditya</span>\r\n</p>\r\n<span style="font-size: 12pt; line-height: 107%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Sumber : Media Indag Vol. 10, No.37</span><!--[if gte mso 9]><xml>\r\n<w:WordDocument>\r\n<w:View>Normal</w:View>\r\n<w:Zoom>0</w:Zoom>\r\n<w:TrackMoves/>\r\n<w:TrackFormatting/>\r\n<w:PunctuationKerning/>\r\n<w:ValidateAgainstSchemas/>\r\n<w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n<w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n<w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n<w:DoNotPromoteQF/>\r\n<w:LidThemeOther>IN</w:LidThemeOther>\r\n<w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n<w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n<w:Compatibility>\r\n<w:BreakWrappedTables/>\r\n<w:SnapToGridInCell/>\r\n<w:WrapTextWithPunct/>\r\n<w:UseAsianBreakRules/>\r\n<w:DontGrowAutofit/>\r\n<w:SplitPgBreakAndParaMark/>\r\n<w:EnableOpenTypeKerning/>\r\n<w:DontFlipMirrorIndents/>\r\n<w:OverrideTableStyleHps/>\r\n</w:Compatibility>