INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(276, 22, 'Ditya', 'Demam Akik', 'demam-akik', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 36pt; line-height: 115%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%">Akhir-akhir\r\nini kita dihebohkan berita media televise maupun media cetak dengan penemuan\r\nbatu giok seberat 1 ton di sebuah pegunungan di Sumatera. Berita ini makin\r\nmenambah animo masyarakat untuk berburu batu yang dipercaya mempunyai tuah atau\r\nbisa membawa keberuntungan bagi pemakainya. Demam batu akik tampaknya masih\r\nakan berlanjut. Dengan banyaknya jenis batu akik yang terdapat di hampir\r\nseluruh wilayah di Indonesia, sepertinya Negara tercinta kita ini akan menjadi surga\r\nbagi pecinta batu akik. Tak hanya diburu para kolektor domestik, namun para\r\nkolektor batu mulia dari mancanegara sudah mulai masuk ke Indonesia.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt; line-height: 115%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%">Demam\r\nbatu akik berdampak positif bagi industri kerajinan batu akik. Di Jawa Timur\r\nsendiri di setiap daerah mempunyai potensi batu dengan ciri khas masing-masing.\r\nPacitan yang mendahului jauh sebelum booming batu akhir-akhir ini. Daerah perbatasan\r\nKabupaten Pacitan, Wonogiri dan Gunung Kidul kaya sumber daya alam berupa bahan\r\nbaku kerajinan.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt; line-height: 115%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%">Upaya\r\npemerintah untuk mendorong perajin dalam memperoleh nilai tambah dari batu\r\nmulia harus didukung oleh semua pihak. Selama ini batu akik dari Pacitan masih\r\nterjual dalam bongkahan tanpa melalui pengolahan. Pacitan sebagai salah satu\r\nsumber bahan baku batu mulia berkualitas di Indonesia masih memerlukan banyak\r\nsentuhan untuk mengoptimalisasi industri kerajinan.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt; line-height: 115%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%">Dari\r\nbanyaknya jenis batu akik, ada beberapa jenis yang sangat digemari oleh para\r\npecinta batu akik. Seperti jenis batu akik bacan hijau, batu akik kalsedon,\r\nbatu akik kalimaya, batu akik safir, batu akik zamrud.</span><!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n</p>\r\n<p style="text-align: justify; text-indent: 36pt; line-height: 115%" class="MsoNormal">\r\n<!--[if gte mso 9]><xml>\r\n<w:WordDocument>\r\n<w:View>Normal</w:View>\r\n<w:Zoom>0</w:Zoom>\r\n<w:TrackMoves/>\r\n<w:TrackFormatting/>\r\n<w:PunctuationKerning/>\r\n<w:ValidateAgainstSchemas/>\r\n<w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n<w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n<w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n<w:DoNotPromoteQF/>\r\n<w:LidThemeOther>IN</w:LidThemeOther>\r\n<w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n<w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n<w:Compatibility>\r\n<w:BreakWrappedTables/>\r\n<w:SnapToGridInCell/>\r\n<w:WrapTextWithPunct/>\r\n<w:UseAsianBreakRules/>\r\n<w:DontGrowAutofit/>\r\n<w:SplitPgBreakAndParaMark/>\r\n<w:EnableOpenTypeKerning/>\r\n<w:DontFlipMirrorIndents/>\r\n<w:OverrideTableStyleHps/>\r\n</w:Compatibility>\r\n<m:mathPr>\r\n<m:mathFont m:val="Cambria Math"/>\r\n<m:brkBin m:val="before"/>\r\n<m:brkBinSub m:val="--"/>\r\n<m:smallFrac m:val="off"/>\r\n<m:dispDef/>\r\n<m:lMargin m:val="0"/>\r\n<m:rMargin m:val="0"/>\r\n<m:defJc m:val="centerGroup"/>\r\n<m:wrapIndent m:val="1440"/>\r\n<m:intLim m:val="subSup"/>\r\n<m:naryLim m:val="undOvr"/>\r\n</m:mathPr></w:WordDocument>\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<w:LatentStyles DefLockedState="false" DefUnhideWhenUsed="false"\r\nDefSemiHidden="false" DefQFormat="false" DefPr