INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(279, 22, 'Pipit', 'Pemberlakuan PPN Ekspor Kopi Ekspor Kopi Jatim Diprediksi Meningkat', 'pemberlakuan-ppn-ekspor-kopi-ekspor-kopi-jatim-diprediksi-meningkat', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Eksportir kopi Jawa\r\nTimur memprediksi volume ekspor kopi tahun ini bakal meningkat hingga 25% meski\r\npemerintah memberlakukan Pengenaan Pajak Nilai (PPN) 10% terhadap ekspor\r\nkomoditi kopi yang sudah diberlakuan sejak 2014 silam. Kebijakan yang diambil pemerintah ini tentu bukan tanpa tujuan.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Pengenaan pajak ini\r\nterkait dengan upaya pemerintah meningkatkan target pajak dari sumber-sumber\r\nbaru. Komoditi kopi Indonesia selama ini bebas dari pengenaan PPN, yang\r\ndiantaranya dimaksutkan untuk meningkatkan volume ekspor dari produk pertanian\r\ndan perkebunan. </span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Direktur Utama PT\r\nGemilang Jaya Makmur perusahaan eksportir kopi asal Malang, Jawa Timur, Rudi\r\nSukoyo mengatakan sejak diberlakukannya aturan tersebut para eksportir kopi\r\nmenghitung ulang harga ekspor kopi tahun ini agar tidak sampai rugi.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">&ldquo;Semangat para petani\r\nmemang tidak menurun, tapi pengenaan PPN ini terpaksa dikompensasikan terhadap\r\nharga kopi mulai dari tingkat petani, yang tentu akan berpengaruh pada harga\r\nkopi ekspor&rdquo;, katanya di Surabaya, Rabu (15/4/2015)</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Biasanya harga kopi\r\nditingkat petani dibeli seharga Rp25.000/Kg untuk jenis robusta, tapi setelah\r\npengenaan PPN harganya turun menjadi Rp23.000-24.000/Kg. Kondisi ini tentu\r\nsedikit banyak berpengaruh terhadap produktivitas kopi yang dihasilkan petani.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Ketua Gaeki Hutama\r\nSugandhi mengatakan asosiasi berencana mendesak kembali pemerintah untuk\r\nmenghapus PPN terhadap produk kopi. Sebelumnya, asosiasi dan petani juga sudah\r\nmengajukan permohonan namun belum ada tanggapan dari pemerintah.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.25in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%">Meskipun pengenaan PPN\r\nini tidak akan berpengaruh signifikan terhadap ekspor kopi, namun baik\r\neksportir maupun petani tetap mengharapkan penghapusan PPN 10% terhadap produk\r\nkopi. Hal ini dilakukan untuk menjaga agar ekspor kopi tetap stabil dengan\r\nkualitas yang terjamin. <span>&nbsp;</span></span>\r\n</p>\r\n<p style="text-align: justify" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 107%">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 107%">Penulis<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>:\r\n-</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 107%">Editor<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>:\r\nPipit Eriyanto</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 107%">Sumber<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>: Media Indag Vol.X N0.37 Maret\r\n2015</span>\r\n</p>\r\n<!--[if gte mso 9]><xml>\r\n<w:WordDocument>\r\n<w:View>Normal</w:View>\r\n<w:Zoom>0</w:Zoom>\r\n<w:TrackMoves/>\r\n<w:TrackFormatting/>\r\n<w:PunctuationKerning/>\r\n<w:ValidateAgainstSchemas/>\r\n<w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n<w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n<w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n<w:DoNotPromoteQF/>\r\n<w:LidThemeOther>EN-US</w:LidThemeOther>\r\n<w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n<w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n<w:Compatibility>\r\n<w:BreakWrappedTables/>\r\n<w:SnapToGridInCell/>\r\n<w:WrapTextWithPunct/>\r\n<w:UseAsianBreakRules/>\r\n<w:D