INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(281, 22, 'Pipit', 'Jatim Pintu Gerbang MEA', 'jatim-pintu-gerbang-mea', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Provinsi Jawa Timur &ldquo;mengklaim&rdquo; paling\r\nsiap memasuki Masyarakat Ekonomi ASEAN atau disebut MEA yang akan diberlakukan\r\npada penghujung tahun 2015. Jawa Timur sudah tidak sabar menunggu instruksi\r\nJakarta, sementara MEA sudah didepan mata dengan bebagai resiko persaingan yang\r\nakan dihadapi Indonesia. </span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&ldquo;Kita tidak mungkin diam melihat\r\nperkembangan zaman. Disatu sisi sosial suasana dan kebijakan global, disisi\r\nlain kesejahteraan masyarakat menjadi perhatian. Karena itu kita melakukan\r\nhijrah pemikiran,&rdquo; kata Gubernur Jawa Timur, Soekarwo, yang lekat disapa Pak De\r\nKarwo<span>&nbsp; </span>ketika meluncurkan buku &ldquo;Pintu\r\nGerbang MEA 2015 harus dibuka&rdquo;di Hotel Mercure.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Pernyataan ini sejalan dengan masih\r\nbelum digerakkannya roda ekonomi Indonesia menghadapi MEA. Menteri Perdagangan\r\nRachmat Gobel ketika membuka <em>the 17<sup>th</sup>\r\nJakarta International Handicraft Trade Center (Inacraft) </em>2015 di <em>Jakarta Convention Center</em> (JCC), Senin\r\n(23/2/2015) masih mengkhawatirkan kesiapan Indonesia menghadapi Masyarakat\r\nEkonomi ASEAN.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Transaksi perdagangan Indonesia dengan Negara-negara\r\nanggota ASEAN saat ini masih mengantongi defisit, hanya dengan Filipina saja\r\nIndonesia mendapatkan surplus pada neraca perdagangan.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Posisi Jawa Timur sedikit berbeda di\r\natas Kamboja, Papua Nugini, Laos dan Timur Leste. Dari postur ekonomi yang\r\nbesar inilah Jawa Timur bergegas mencapai garis depan sementara Jakarta seperti\r\ngamang mengahadapi MEA.&rdquo;Kita tidak mungkin diam melihat perkembangan zaman. disisi\r\nlain kesejahteraan masyarakat harus menjadi perhatian,&rdquo; ujar Pak De.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&ldquo;Karenanya, menjadi superkoridor adalah\r\ntuntutan globalisasi. Selain menjadi basis perdagangan, industri, investasi dan\r\ntenaga kerja ahli, Jatim juga harus membangun regulasi yang bisa memihak kepada\r\npelayanan publik,&rdquo; tambahnya.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Secara regional, Jawa Timur memilki\r\nkekuatan sebagai koridor besar MEA di Indonesia. Catatan Badan Pusat Statistika\r\nJawa Timur, menunjukan neraca perdagangan Jawa Timur di ASEAN sampai dengan\r\nkuartal kedua 2014 masih <em>leading</em> dari\r\nKamboja, Laos, Myanmar, Vietnam dan Malaysia. Jawa Timur hanya kalah dengan\r\nThailand dan Singapura saja.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 22.5pt; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Penulis<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>:\r\n-</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Editor<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>:\r\nPipit Eriyanto</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in; line-height: 150%" class="MsoNormal">\r\n<span style="font-size: 10pt; line-height: 150%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Sumber<span>&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>: Media Indag Vol.X N0.37 Maret\r\n2015</span>\r\n</p>\r\n<!--[if gte mso 9]><xml>\r\n<w:WordDocument>\r\n<w:View>Normal</w:View>\r\n<w:Zoom>0</w:Zoom>\r\n<w:TrackMoves/>\r\n<w:TrackFormatting/>\r\n<w:PunctuationKerning/>\r\n<w:ValidateAgainstSchemas/>\r\n<w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n<w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n<w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n<w:DoNotPromoteQF/>\r\n<w:LidThemeOther>EN-US</w:LidThemeOther>\r\n<w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n<w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n<w:Compatibility>\r\n<w:BreakWrappedTables/>\r\n<w:SnapToGridInCell/>