INSERT INTO `berita` (`id_berita`, `id_kategori`, `username`, `judul`, `judul_seo`, `headline`, `isi_berita`, `hari`, `tanggal`, `jam`, `gambar`, `dibaca`, `tag`, `status_broadcast`) VALUES
(300, 22, 'admin', '  PROVINSI INDUSTRI AGRO ON FARM', '--provinsi-industri-agro-on-farm', 'Y', '<!--[if gte mso 9]><xml>\r\n<o:OfficeDocumentSettings>\r\n<o:AllowPNG/>\r\n</o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Gubernur\r\nJawa Timur, DR H.Soekarwo Senin (14/2) mencetuskan gagasan akan menjadikan Jawa\r\nTimur sebagai provinsi industry agro pn farm. Gagasan ini sebagai pengembangan\r\ndari visi provinsi Agro Industri yang telah ditetapkan dalam Rencana\r\nPembangunan Jangka Menengah<span>&nbsp; </span>Daerah\r\n(RPJMD). Dalam RPJMD Jatim yang ditetapkan Oktober lalu di antaranya\r\nmenguraikan visi untuk menjadikan jatim sebagai Provinsi Agro Industri\r\nterkemuka di Asia, &ldquo;Itu kan namanya visi, akan terus dikejar. Lain dengan\r\nprogram, ada batas waktunya,&rdquo; ujar Soekarwo, Senin (5/10)</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Inovasi\r\ndan kreasi diperlukan untuk menghasilkan makanan olahan dari produk pertanian.\r\n&ldquo;Apalagi persaingan di MEA sangat ketat, &ldquo;tambah gubernur. &ldquo;Sentuhan kreatif\r\npada kemasan ikut menentukan daya saing produk makanan olahan.&rdquo; Dengan\r\ndemikian, kata gubernur, pendapatan generic bisa naik sehingga kemampuan untuk\r\nbelanja petani pun naik. Nantinya, sebagian pendapatan harus disimpan dan\r\nditabung sebagai bekal di kemudian hari. &ldquo;jadi jangan sampai dihabiskan\r\nsemuanya untuk belanja konsumsi,&rdquo; tuturnya.</span>\r\n</p>\r\n<p style="text-align: justify; text-indent: 0.5in" class="MsoNormal">\r\n<span style="font-size: 12pt; line-height: 115%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Pertumbuhan\r\nproduk makanan olahan di Jawa Timur selama lima tahun berjalan di atas\r\nrata-rata pertumbuhan nasional. Sampai dengan semester III/2015 pertumbuhan industri\r\nmakanan olahan di Jawa Timur tercatat sebesar 5,6 persen dibanding pertumbuhan\r\nnasional sebesar 4,22 persen.</span>\r\n</p>\r\n<p style="text-align: center" class="MsoNormal" align="center">\r\n<span style="font-size: 12pt; line-height: 115%; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">Pertumbuhan\r\nIndustri Olahan Jawa Timur </span>\r\n</p>\r\n<table border="1" cellspacing="0" cellpadding="0" class="MsoTableGrid" style="margin-left: 117.25pt; border-collapse: collapse; border: medium none">\r\n	<tbody>\r\n		<tr>\r\n			<td width="97" valign="top" style="width: 72.65pt; border: 1pt solid windowtext; padding: 0in 5.4pt">\r\n			<p style="margin-bottom: 0.0001pt; text-align: center; line-height: normal" class="MsoNormal" align="center">\r\n			<span style="font-size: 12pt; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">TAHUN</span>\r\n			</p>\r\n			</td>\r\n			<td width="114" valign="top" style="width: 85.5pt; border-width: 1pt 1pt 1pt medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; -moz-border-top-colors: none; -moz-border-right-colors: none; -moz-border-bottom-colors: none; -moz-border-left-colors: none; border-image: none; padding: 0in 5.4pt">\r\n			<p style="margin-bottom: 0.0001pt; text-align: center; line-height: normal" class="MsoNormal" align="center">\r\n			<span style="font-size: 12pt; font-family: &quot;Times New Roman&quot;,&quot;serif&quot;">NASIONAL/%</span>\r\n			</p>\r\n			</td>\r\n			<td width="101" valign="top" style="width: 75.85pt; border-width: 1pt 1pt 1pt medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; -moz-border-top-colors: none; -moz-border-right-colors: none; -moz-border-bottom-colors: none; -moz-border-left-colors: none; border-image: none; 